describe('MyTestSuit', function() 
{
    it('Verify Tilte Of The Page- positive', function() 
    {
      cy.visit("https://www.airbnb.com/")
      // cy.title().contains('Airbnb')
  })
  // it('Close Pop UP', function() 
  //   {
  //     cy.get("button[title='OK']").as('popup')
  //     cy.wait('@popup')
  //     cy.get("button[title='OK']").click()
  // })
  it('enter value in where and click on Option', function() 
    {
      cy.get('._26hc67j ._1f8ev6q').type('Sicily, Italy')
      cy.get("._qcpa4n span:contains('Sicily, Italy experiences')").click()
  })
  it('handle the checkin date', function() 
    {
      for (var i = 1; i < 15; i++) {
      cy.get("._1svux14[data-visible='true']  strong").then(elem => {
          if (elem.text() == 'April 2020') {
            cy.get("._1svux14[data-visible='true'] td[aria-label*='April 2, 2020']").click()
          }
          else {
          cy.get("._1h5uiygl").click()
           }
          })
        }
   })
   it('handle the checkout date', function() 
   {
     for (var i = 1; i < 15; i++) {
     cy.get("._1svux14[data-visible='true']  strong").then(elem => {
         if (elem.text() == 'April 2020') {
           cy.get("._1svux14[data-visible='true'] td[aria-label*='April 9, 2020']").click()
                
         }
         else {
         cy.get("._1h5uiygl").click()
          }
         })
       }
  })
  it('handle guests drop down', function() 
    {
      cy.get('._26hc67j ._j1kt73').click()
      cy.get("._26hc67j div[aria-labelledby*='adults'] ._1a72ixey").click()
      cy.get("._26hc67j div[aria-labelledby*='children'] ._1a72ixey").click()
      cy.get('._26hc67j ._j1kt73').click()
  })
  it('clcik on submit', function() 
    {
      cy.get("button[type='submit']").click()
  })
  it('clcik on price', function() 
    {
      cy.get("#menuItemButton-price_range").click()
      cy.get("#price_filter_min").clear().type('300')
      cy.get("#price_filter_max").clear().type('3000')
      cy.get("#filter-panel-save-button").click()
  })
  it('experiences for given selection', function() 
    {
      cy.get("._1xupcs2").eq(1) .click()
      cy.go('back')
      cy.get("._1xupcs2").eq(3).click()
      cy.go('back')
  })
})